const setTime=e=>{const t=document.querySelector(".timer__time");let n=e.getHours(),s=e.getMinutes();t.textContent=n<10?`0${n}:${s}`:`${n}:${s}`,t.textContent=s<10?`${n}:0${s}`:`${n}:${s}`};setTime(new Date);const setTimer=()=>{let e,t=60-(new Date).getSeconds();t>=1?(t*=1e3,e=setInterval((()=>{setTime(new Date)}),t),setTimeout((()=>{clearInterval(e),t=6e4,setInterval((()=>{setTime(new Date)}),t)}),t)):0===t&&setInterval((()=>{setTime(new Date)}),t)};setTimer();const greetingSetter=e=>{const t=document.querySelector(".timer__daytime"),n=document.querySelector(".screen");e.getHours()>=22&&e.getHours()<=23||e.getHours()>=0&&e.getHours()<6?(t.textContent="Good night",n.classList.add("screen--nighttime")):e.getHours()>=6&&e.getHours()<10?(t.textContent="Good morning",n.classList.add("screen--daytime")):e.getHours()>=10&&e.getHours()<16?(t.textContent="Good afternoon",n.classList.add("screen--daytime")):e.getHours()>=16&&e.getHours()<22&&(t.textContent="Good evening",n.classList.add("screen--nighttime"))};greetingSetter(new Date);